table.definition:
CREATE TABLE players (
	
	objectId		INT				NOT NULL,
	name			VARCHAR(35)		NOT NULL,
	accountName		VARCHAR(45)		NOT NULL,
	online			BOOLEAN			NOT NULL	DEFAULT false,
	mainClassId		SMALLINT		NOT NULL,
	activeClassId	SMALLINT		NOT NULL,
	
	PRIMARY KEY (objectId)
);
CREATE UNIQUE INDEX name			ON players (name);
CREATE        INDEX accountName		ON players (accountName);

CREATE TABLE player_appearances (
objectId INT NOT NULL, gender SMALLINT NOT NULL, face SMALLINT NOT NULL, hairColor SMALLINT NOT NULL, hairStyle SMALLINT NOT NULL,
PRIMARY KEY (objectId) );

CREATE TABLE player_positions (
objectId INT NOT NULL, x INT, y INT, z INT, heading INT,
PRIMARY KEY (objectId) );
